import { type Income, type InsertIncome, type Expense, type InsertExpense, type Asset, type InsertAsset } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Income methods
  getIncome(id: string): Promise<Income | undefined>;
  getAllIncome(): Promise<Income[]>;
  createIncome(income: InsertIncome): Promise<Income>;
  updateIncome(id: string, income: Partial<InsertIncome>): Promise<Income | undefined>;
  deleteIncome(id: string): Promise<boolean>;

  // Expense methods
  getExpense(id: string): Promise<Expense | undefined>;
  getAllExpenses(): Promise<Expense[]>;
  createExpense(expense: InsertExpense): Promise<Expense>;
  updateExpense(id: string, expense: Partial<InsertExpense>): Promise<Expense | undefined>;
  deleteExpense(id: string): Promise<boolean>;

  // Asset methods
  getAsset(id: string): Promise<Asset | undefined>;
  getAllAssets(): Promise<Asset[]>;
  createAsset(asset: InsertAsset): Promise<Asset>;
  updateAsset(id: string, asset: Partial<InsertAsset>): Promise<Asset | undefined>;
  deleteAsset(id: string): Promise<boolean>;
}

export class MemStorage implements IStorage {
  private incomeMap: Map<string, Income>;
  private expenseMap: Map<string, Expense>;
  private assetMap: Map<string, Asset>;

  constructor() {
    this.incomeMap = new Map();
    this.expenseMap = new Map();
    this.assetMap = new Map();
  }

  // Income methods
  async getIncome(id: string): Promise<Income | undefined> {
    return this.incomeMap.get(id);
  }

  async getAllIncome(): Promise<Income[]> {
    return Array.from(this.incomeMap.values()).sort((a, b) => 
      new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
    );
  }

  async createIncome(insertIncome: InsertIncome): Promise<Income> {
    const id = randomUUID();
    const income: Income = {
      ...insertIncome,
      id,
      createdAt: new Date(),
    };
    this.incomeMap.set(id, income);
    return income;
  }

  async updateIncome(id: string, updateData: Partial<InsertIncome>): Promise<Income | undefined> {
    const existing = this.incomeMap.get(id);
    if (!existing) return undefined;

    const updated: Income = { ...existing, ...updateData };
    this.incomeMap.set(id, updated);
    return updated;
  }

  async deleteIncome(id: string): Promise<boolean> {
    return this.incomeMap.delete(id);
  }

  // Expense methods
  async getExpense(id: string): Promise<Expense | undefined> {
    return this.expenseMap.get(id);
  }

  async getAllExpenses(): Promise<Expense[]> {
    return Array.from(this.expenseMap.values()).sort((a, b) => 
      new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
    );
  }

  async createExpense(insertExpense: InsertExpense): Promise<Expense> {
    const id = randomUUID();
    const expense: Expense = {
      ...insertExpense,
      id,
      createdAt: new Date(),
    };
    this.expenseMap.set(id, expense);
    return expense;
  }

  async updateExpense(id: string, updateData: Partial<InsertExpense>): Promise<Expense | undefined> {
    const existing = this.expenseMap.get(id);
    if (!existing) return undefined;

    const updated: Expense = { ...existing, ...updateData };
    this.expenseMap.set(id, updated);
    return updated;
  }

  async deleteExpense(id: string): Promise<boolean> {
    return this.expenseMap.delete(id);
  }

  // Asset methods
  async getAsset(id: string): Promise<Asset | undefined> {
    return this.assetMap.get(id);
  }

  async getAllAssets(): Promise<Asset[]> {
    return Array.from(this.assetMap.values()).sort((a, b) => 
      new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
    );
  }

  async createAsset(insertAsset: InsertAsset): Promise<Asset> {
    const id = randomUUID();
    const asset: Asset = {
      ...insertAsset,
      id,
      createdAt: new Date(),
      description: insertAsset.description || null,
    };
    this.assetMap.set(id, asset);
    return asset;
  }

  async updateAsset(id: string, updateData: Partial<InsertAsset>): Promise<Asset | undefined> {
    const existing = this.assetMap.get(id);
    if (!existing) return undefined;

    const updated: Asset = { ...existing, ...updateData };
    this.assetMap.set(id, updated);
    return updated;
  }

  async deleteAsset(id: string): Promise<boolean> {
    return this.assetMap.delete(id);
  }
}

export const storage = new MemStorage();

